import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import TextField from "@material-ui/core/TextField";
import "./App.css";

const socket = io.connect("http://localhost:4000/");
// ... (existing imports)

// ... (existing imports)

const GameComponent = ({ onSubmit, onAllResponsesSubmitted, gameState = { responses: {}, shuffledImages: [] } }) => {
  const [inputValue, setInputValue] = useState("");
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [round, setRound] = useState(0);
  const [guessedPrompts, setGuessedPrompts] = useState([]);

  const onInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const onSubmitClick = (e) => {
    e.preventDefault();
    onSubmit(inputValue);
    setInputValue("");
    setIsSubmitted(true);
  };

  const onGuessClick = (guessedPrompt) => {
    // Update the guessed prompts for the current round
    setGuessedPrompts((prevGuessedPrompts) => [...prevGuessedPrompts, guessedPrompt]);

    // Move to the next round if all clients have guessed
    if (guessedPrompts.length === gameState.shuffledImages.length - 1) {
      setRound((prevRound) => prevRound + 1);
      setGuessedPrompts([]);
    }
  };

  useEffect(() => {
    if (isSubmitted) {
      onAllResponsesSubmitted();
    }
  }, [isSubmitted, onAllResponsesSubmitted]);

  return (
    <div>
      <h1>{isSubmitted ? "Waiting for Others..." : "New Game Component"}</h1>
      {isSubmitted ? (
        <p>Your response has been submitted. Waiting for others...</p>
      ) : (
        <form onSubmit={onSubmitClick}>
          <TextField
            name="gameInput"
            onChange={onInputChange}
            value={inputValue}
            label="Enter Something"
          />
          <button type="submit">Submit</button>
        </form>
      )}
      {gameState.shuffledImages.length > 0 && round < gameState.shuffledImages.length && (
        <div>
          <h2>Round {round + 1}</h2>
          {guessedPrompts.includes(gameState.shuffledImages[round].name) ? (
            <p>You guessed correctly!</p>
          ) : (
            <div>
              <img src={gameState.shuffledImages[round].imageUrl} alt={`Round ${round + 1}`} />
              <button onClick={() => onGuessClick(gameState.shuffledImages[round].name)}>
                Guess the Prompt
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

const App = () => {
  const [state, setState] = useState({
    message: "",
    name: "",
    roomCode: "",
    gameStarted: false,
    canJoinRoom: true,
  });
  const [chat, setChat] = useState([]);
  const [roomUsers, setRoomUsers] = useState([]);
  const [showGameComponent, setShowGameComponent] = useState(false);
  const [allResponsesSubmitted, setAllResponsesSubmitted] = useState(false);

  useEffect(() => {
    socket.on("message", ({ name, message }) => {
      setChat([...chat, { name, message }]);
    });

    socket.on("roomUsers", (users) => {
      setRoomUsers(users);
    });

    socket.on("alert", (message) => {
      alert(message);
      setState({ ...state, canJoinRoom: true });

      // Add a delay before allowing the user to join again
      setTimeout(() => {
        setState({ ...state, canJoinRoom: true });
      }, 3000);
    });

    socket.on("startGame", () => {
      setState({ ...state, gameStarted: true });
      setShowGameComponent(true);
    });

    socket.on("allResponsesSubmitted", () => {
      setAllResponsesSubmitted(true);
    });
  }, [chat, state]);

  const onTextChange = (e) => {
    setState({ ...state, [e.target.name]: e.target.value });
  };

  const onMessageSubmit = (e) => {
    e.preventDefault();
    const { name, message } = state;
    socket.emit("message", { name, message });
    setState({ ...state, message: "" });
  };

  const onJoinRoom = (e) => {
    e.preventDefault();
    const { name, roomCode, gameStarted, canJoinRoom } = state;

    if (!gameStarted && canJoinRoom) {
      socket.emit("joinRoom", { name, roomCode });
      setState({ ...state, canJoinRoom: false });
    }
  };

  const onStartGame = () => {
    const { roomCode } = state;
    socket.emit("startGame", roomCode);
    setState({ ...state, gameStarted: true });
  };

  const onGameComponentSubmit = (inputValue) => {
    // Handle the submission of the game component input
    socket.emit("submitResponse", { name: state.name, response: inputValue });
  };

  const onAllResponsesSubmitted = () => {
    // This function is called when all users have submitted their responses
    console.log("All responses submitted!");
    setAllResponsesSubmitted(true);
  };

  const renderChat = () => {
    return chat.map(({ name, message }, index) => (
      <div key={index}>
        <h3>
          {name}: <span>{message}</span>
        </h3>
      </div>
    ));
  };

  const renderRoomUsers = () => {
    return roomUsers.map(({ id, name }, index) => (
      <div key={index}>
        <p>{name} (ID: {id})</p>
      </div>
    ));
  };

  return (
    <div className="card">
      {!showGameComponent ? (
        <div>
          <form onSubmit={onJoinRoom}>
            <h1>Join Room</h1>
            <div className="name-field">
              <TextField
                name="name"
                onChange={(e) => onTextChange(e)}
                value={state.name}
                label="Your Name"
              />
            </div>
            <div className="name-field">
              <TextField
                name="roomCode"
                onChange={(e) => onTextChange(e)}
                value={state.roomCode}
                label="Room Code"
              />
            </div>
            <button disabled={state.gameStarted || !state.canJoinRoom}>Join Room</button>
          </form>
          <button onClick={onStartGame} disabled={state.gameStarted}>
            Start Game
          </button>
        </div>
      ) : (
        <GameComponent
          onSubmit={onGameComponentSubmit}
          onAllResponsesSubmitted={onAllResponsesSubmitted}
        />
      )}
      <form onSubmit={onMessageSubmit}>
        {/* ... existing code */}
      </form>
      <div className="render-chat">
        <h1>Chat Log</h1>
        {renderChat()}
      </div>
      <div className="room-users">
        <h1>Room Users</h1>
        {renderRoomUsers()}
      </div>
    </div>
  );
};

export default App;
