import React, { useState, useEffect } from "react";
import io from "socket.io-client";
import TextField from "@material-ui/core/TextField";
import "./App.css";

const socket = io.connect("http://localhost:4000/");

// ... (existing imports)

const App = () => {
  const [state, setState] = useState({
    message: "",
    name: "",
    roomCode: "",
    gameStarted: false,
  });
  const [chat, setChat] = useState([]);
  const [roomUsers, setRoomUsers] = useState([]);

  useEffect(() => {
    socket.on("message", ({ name, message }) => {
      setChat([...chat, { name, message }]);
    });

    socket.on("roomUsers", (users) => {
      setRoomUsers(users);
    });
  }, [chat]);

  const onTextChange = (e) => {
    setState({ ...state, [e.target.name]: e.target.value });
  };

  const onMessageSubmit = (e) => {
    e.preventDefault();
    const { name, message } = state;
    socket.emit("message", { name, message });
    setState({ ...state, message: "" });
  };

  const onJoinRoom = (e) => {
    e.preventDefault();
    const { name, roomCode, gameStarted } = state;

    if (!gameStarted) {
      socket.emit("joinRoom", { name, roomCode });
    }
  };

  const onStartGame = () => {
    const { roomCode } = state;
    socket.emit("startGame", roomCode);
    setState({ ...state, gameStarted: true });
  };

  const renderChat = () => {
    return chat.map(({ name, message }, index) => (
      <div key={index}>
        <h3>
          {name}: <span>{message}</span>
        </h3>
      </div>
    ));
  };

  const renderRoomUsers = () => {
    return roomUsers.map(({ id, name }, index) => (
      <div key={index}>
        <p>{name} (ID: {id})</p>
      </div>
    ));
  };

  return (
    <div className="card">
      <form onSubmit={onJoinRoom}>
        <h1>Join Room</h1>
        <div className="name-field">
          <TextField
            name="name"
            onChange={(e) => onTextChange(e)}
            value={state.name}
            label="Your Name"
          />
        </div>
        <div className="name-field">
          <TextField
            name="roomCode"
            onChange={(e) => onTextChange(e)}
            value={state.roomCode}
            label="Room Code"
          />
        </div>
        <button disabled={state.gameStarted}>Join Room</button>
      </form>
      <button onClick={onStartGame} disabled={state.gameStarted}>
        Start Game
      </button>
      <form onSubmit={onMessageSubmit}>
        {/* ... existing code */}
      </form>
      <div className="render-chat">
        <h1>Chat Log</h1>
        {renderChat()}
      </div>
      <div className="room-users">
        <h1>Room Users</h1>
        {renderRoomUsers()}
      </div>
    </div>
  );
};

export default App;
